sp_widget.client_script:
function($scope,spModal) {
	/* widget controller */
	var c = this;
	$scope.EncodeUP = function(){
		spModal.open({
			title: 'Give me a name',
			message: 'What would you like to name it?',
			input: true,
			value: c.name
		}).then(function(name) {
			c.name = name;
		})

	}
}

